styles.css:
:root {
  --bs-border-width: 2px !important;
  --bs-border-color: #000 !important;
}

[data-bs-theme=light],
[data-bs-theme=dark] {
  --bs-border-color: #000 !important;
}

.table {
  border: 2px solid #000 !important;
}

.navbar {
  font-size: 1.09375rem !important;
  font-weight: 500 !important;
  border: 2px solid #000 !important;
}
.navbar.fixed-top {
  border-width: 0 0 2px 0 !important;
}
.navbar.fixed-bottom {
  border-width: 2px 0 0 0 !important;
}
.navbar .dropdown-toggle::after {
  margin-left: 0.15em !important;
  vertical-align: 0.15em !important;
}
.navbar-toggler {
  --bs-border-width: 2px !important;
}

.btn {
  margin: 3px 0 0 3px !important;
  border-color: #000 !important;
  box-shadow: 3px 3px 0 0 var(--bs-border-color) !important;
  transition: all 0.3s !important;
  transform: translate(-3px, -3px) !important;
}
.btn:hover {
  border-color: #000 !important;
  box-shadow: none !important;
  transform: translate(0, 0) !important;
}
.btn.disabled {
  border-color: #000 !important;
}
.btn-link, .btn-link:hover {
  color: #000 !important;
}

.btn-outline-primary {
  color: #000 !important;
  background-color: #a2e436 !important;
  box-shadow: none !important;
  transform: translate(0, 0) !important;
}
.btn-outline-primary:hover {
  box-shadow: 3px 3px 0 0 var(--bs-border-color) !important;
  transform: translate(-3px, -3px) !important;
}
.btn-check + .btn-primary:hover,
.btn-check + .btn-outline-primary:hover {
  background-color: #a2e436 !important;
}
.btn-outline-secondary {
  color: #000 !important;
  background-color: #fff !important;
  box-shadow: none !important;
  transform: translate(0, 0) !important;
}
.btn-outline-secondary:hover {
  box-shadow: 3px 3px 0 0 var(--bs-border-color) !important;
  transform: translate(-3px, -3px) !important;
}
.btn-check + .btn-secondary:hover,
.btn-check + .btn-outline-secondary:hover {
  background-color: #fff !important;
}
.btn-outline-success {
  color: #000 !important;
  background-color: #68d391 !important;
  box-shadow: none !important;
  transform: translate(0, 0) !important;
