styles.css:
* {
  --bs-primary-text: var(--bs-lighter-text);
  --bs-success-text: var(--bs-lighter-text);
  --bs-warning-text: var(--bs-lighter-text);
}

.has-bs-primary-background-color.wp-element-button,
.btn-primary>.wp-element-button,
.btn-primary:not(.wp-block-button),
.navbar.has-bs-primary-background-color,
.navbar.bg-primary {
  --bs-btn-bg-image: var(--wp--preset--gradient--primary);
  --bs-navbar-background-image: var(--wp--preset--gradient--primary);
  --bs-nav-pills-link-active-background-image: var(--wp--preset--gradient--primary);
}

.is-style-outline>.has-bs-primary-background-color.wp-element-button:hover,
.is-style-outline.bg-primary>.wp-element-button:hover,
.btn-outline-primary:not(.wp-block-button):hover,
.has-bs-primary-background-color.wp-element-button:hover,
.btn-primary>.wp-element-button:hover,
.btn-primary:not(.wp-block-button):hover {
  --bs-btn-hover-bg-image: var(--wp--preset--gradient--primary-hover);
}

