.is-style-outline>.wp-element-button,
[class*="btn-outline"]>.wp-element-button,
[class*="btn-outline"]:not(.wp-block-button),
.wp-element-button,
.btn:not(.wp-block-button) {
  position: relative;
  text-transform: uppercase;
  border-color: transparent !important;
  transition: color 0.4s, background-color 0.4s, border-color 0.4s, box-shadow 0.4s !important;
}

.wp-element-button::before,
.btn:not(.wp-block-button)::before {
  position: absolute;
  top: 0;
  left: 0;
  display: block;
  width: 100%;
  height: 100%;
  margin-left: 0 !important;
  pointer-events: none;
  content: "";
  background-position: 50% !important;
  background-size: 1000% 1000% !important;
  border: none !important;
  opacity: 0 !important;
  transition: background 0.5s, opacity 1s !important;
  background-image: radial-gradient(circle, currentColor 10%, transparent 10.01%) !important;
}

.wp-element-button:active::before,
.btn:not(.wp-block-button):active::before {
  background-size: 0 0 !important;
  opacity: 0.2 !important;
  transition: none !important;
}

.wp-element-button.disabled::before,
.btn:not(.wp-block-button).disabled::before,
.wp-element-button[disabled]::before,
.btn:not(.wp-block-button)[disabled]::before {
  display: none !important;
}

