.form-check-input:not(:focus) {
  border: 2px solid var(--bs-dark-bg-subtle) !important;
}

.form-check-input:focus {
  box-shadow: var(--bs-box-shadow-button) !important;
}

.form-switch .form-check-input {
  position: relative;
  height: 0.8em;
  margin-top: 0.29em;
  background-color: var(--bs-dark-bg-subtle);
  background-image: none;
  border: none;
}

.form-switch .form-check-input:focus {
  box-shadow: none;
}

.form-switch .form-check-input::before {
  position: absolute;
  top: -0.2em;
  left: -0.2em;
  width: 1.2em;
  height: 1.2em;
  content: "";
  background-color: #fff;
  border-radius: 50%;
  box-shadow: 0 1px 4px rgba(0, 0, 0, 0.3);
  transition: left 0.15s ease-in-out;
}

.form-switch .form-check-input::after {
  position: absolute;
  top: -0.2em;
  left: -0.2em;
  z-index: -1;
  width: 1.2em;
  height: 1.2em;
  content: "";
  border-radius: 50%;
  box-shadow: 0 0 0 9px rgba(0, 0, 0, 0.05);
  transition: left 0.15s ease-in-out, transform 0.15s ease-in-out, box-shadow 0.15s ease-in-out;
  transform: scale(0);
}

.form-switch .form-check-input:hover:not(.disabled)::after,
.form-switch .form-check-input:focus:not(.disabled)::after {
  transform: scale(0.9);
}

.form-switch .form-check-input:checked::before {
  left: calc(100% - 0.8em);
}

.form-switch .form-check-input:checked::after {
  left: calc(100% - 0.8em);
}

.form-switch .form-check-input:checked:hover:not(.disabled)::after,
.form-switch .form-check-input:checked:focus:not(.disabled)::after {
  transform: scale(0.9);
}

