styles.css:
body {
  background-image: var(--wp--preset--gradient--body);
}
 body, [data-bs-theme=light] {
  --bs-secondary-text: var(--bs-white);
  --bs-success-text: var(--bs-white);
  --bs-info-text: var(--bs-white);
  --bs-warning-text: var(--bs-white);
}
.bg-body-secondary,
.has-bs-secondary-bg-background-color,
.nav-tabs .nav-item .nav-link.active,
.nav-tabs .nav-item.show .nav-link,
.accordion-item,
.alert,
/*
.card,
.dropdown-menu,
.list-group-item,
*/
.modal-content,
.toast,
.popover,
.tooltip,
.offcanvas {
  -webkit-backdrop-filter: blur(5px);
  backdrop-filter: blur(5px);
}

.card > .list-group:not([class*="list-group-item-"]) ~ li,
.card > .list-group:not([class*="list-group-item-"]) > .list-group-item:where(:not([class*="list-group-item-"])),
.card > .list-group:not([class*="list-group-item-"]) > .list-group-item.list-group-item-action:not([class*="-primary"]):not([class*="-secondary"]):not([class*="-success"]):not([class*="-info"]):not([class*="-warning"]):not([class*="-danger"]):not([class*="-light"]):not([class*="-dark"]) {
  --bs-list-group-background-image: none;
  --bs-list-group-bg: none;
  -webkit-backdrop-filter: none;
  backdrop-filter: none;
}
.list-group-item[class*="-primary"],
.list-group-item[class*="-secondary"],
.list-group-item[class*="-success"],
.list-group-item[class*="-info"],
.list-group-item[class*="-warning"],
.list-group-item[class*="-danger"],
.list-group-item[class*="-light"],
.list-group-item[class*="-dark"],
.list-group[class*="-primary"] li,
.list-group[class*="-secondary"] li,
.list-group[class*="-success"] li,
.list-group[class*="-info"] li,
.list-group[class*="-warning"] li,
.list-group[class*="-danger"] li,
.list-group[class*="-light"] li,
.list-group[class*="-dark"] li {
  --bs-list-group-background-image: none;
}
.list-group-item.active {
  -webkit-backdrop-filter: none;
  backdrop-filter: none;
}
.wp-block-quote:not(.has-background) {
background-image: var(--wp--preset--gradient--element);
}
.wp-block-quote:not(.has-background),
.bg-body-secondary,
.has-bs-secondary-bg-background-color {
  background-color: rgba(255, 255, 255, 0.3) !important;
}
.alert {
  position: relative;
  overflow: hidden;
}
.alert::after {
  position: absolute;
  top: 0;
  left: 0;
  width: 0.5rem;
  height: 100%;
  content: "";
  background-color: var(--bs-primary);
}
.alert-primary {
  border: none;
  background-color:rgba(var(--bs-primary-bg-subtle-rgb), 0.3);
}
.alert-primary::after {
  background-color: var(--bs-primary);
}
.alert-secondary {
  border: none;
  color: var(--bs-dark);
  background-color: rgba(var(--bs-secondary-bg-subtle-rgb), 0.3);
}
.alert-secondary::after {
  background-color: var(--bs-secondary);
}
.alert-success {
  border: none;
  background-color:rgba(var(--bs-success-bg-subtle-rgb), 0.3);
}
.alert-success::after {
  background-color: var(--bs-success);
}
.alert-info {
  border: none;
  background-color:rgba(var(--bs-info-bg-subtle-rgb), 0.3);
}
.alert-info::after {
  background-color: var(--bs-info);
}
.alert-warning {
  border: none;
  background-color:rgba(var(--bs-warning-bg-subtle-rgb), 0.3);
}
.alert-warning::after {
  background-color: var(--bs-warning);
}
.alert-danger {
  border: none;
  background-color:rgba(var(--bs-danger-bg-subtle-rgb), 0.3);
}
.alert-danger::after {
  background-color: var(--bs-danger);
}
.alert-light {
  border: none;
  color: var(--bs-dark);
  background-color:rgba(var(--bs-light-bg-subtle-rgb), 0.3);
}
.alert-light::after {
  background-color: var(--bs-light);
}
.alert-dark {
  border: none;
  background-color:rgba(var(--bs-dark-bg-subtle-rgb), 0.3);
}
.alert-dark::after {
  background-color: var(--bs-dark);
}
.badge.text-bg-light, .badge.bg-light, .badge.has-bs-light-background-color {
  color: var(--bs-dark) !important;
}
p.btn-close,
.btn-close {
  --bs-btn-close-color: #fff !important;
  --bs-btn-close-bg: url("data:image/svg+xml,%3csvg xmlns='http://www.w3.org/2000/svg' viewBox='0 0 16 16' fill='%23fff'%3e%3cpath d='M.293.293a1 1 0 0 1 1.414 0L8 6.586 14.293.293a1 1 0 1 1 1.414 1.414L9.414 8l6.293 6.293a1 1 0 0 1-1.414 1.414L8 9.414l-6.293 6.293a1 1 0 0 1-1.414-1.414L6.586 8 .293 1.707a1 1 0 0 1 0-1.414z'/%3e%3c/svg%3e") !important;
}
.card.bg-light .card-header,
.card.bg-light .card-body {
  color: var(--bs-dark) !important;
}
.card.bg-primary,
.card.bg-success,
.card.bg-info,
.card.bg-warning,
.card.bg-danger,
.card.bg-light,
.card.bg-dark {
  background-image: none !important;
}
.form-control::-webkit-file-upload-button {
  background-color: transparent;
  background-image: var(--wp--preset--gradient--element);
  -webkit-backdrop-filter: blur(5px);
  backdrop-filter: blur(5px);
  border: none;
}
.form-control::file-selector-button {
  background-color: transparent;
  background-image: var(--wp--preset--gradient--element);
  -webkit-backdrop-filter: blur(5px);
  backdrop-filter: blur(5px);
  border: none;
}
.form-control:hover:not(:disabled):not([readonly])::-webkit-file-upload-button {
  background-color: rgba(255, 255, 255, 0.1);
}
.form-control:hover:not(:disabled):not([readonly])::file-selector-button {
  background-color: rgba(255, 255, 255, 0.1);
}
.input-group-text {
  background-color: transparent;
  background-image: var(--wp--preset--gradient--element);
  box-shadow: inset 1px 1px rgba(255, 255, 255, 0.2), inset -1px -1px rgba(255, 255, 255, 0.1);
}
.input-group:not(.has-validation) > :not(:last-child):not(.dropdown-toggle):not(.dropdown-menu),
.input-group:not(.has-validation) > .dropdown-toggle:nth-last-child(n+3) {
  border-right: none;
}
.form-select:not([multiple]) {
  transition: border-color 0.15s ease-in-out;
}
.form-select:not([multiple]) option {
  color: #000;
}
.text-bg-light .form-control, .bg-light .form-control, .has-bs-light-background-color .form-control {
  color: #343a40;
  border-color: rgba(0, 0, 0, 0.2) !important;
}
.text-bg-light .form-control::-moz-placeholder, .bg-light .form-control::-moz-placeholder, .has-bs-light-background-color .form-control::-moz-placeholder {
  color: rgba(0, 0, 0, 0.2) !important;
}
.text-bg-light .form-control::placeholder, .bg-light .form-control::placeholder, .has-bs-light-background-color .form-control::placeholder {
  color: rgba(0, 0, 0, 0.2) !important;
}
.list-group-item-action.active {
  background-color: var(--bs-list-group-active-bg) !important;
}
.nav-tabs .nav-item .nav-link.active a,
.nav-tabs .nav-item.show .nav-link a {
  color: #fff;
}
.nav-tabs .nav-item .nav-link.active .text-muted,
.nav-tabs .nav-item.show .nav-link .text-muted {
  color: rgba(255, 255, 255, 0.7) !important;
}
.nav-tabs .nav-item .nav-link.active:hover, .nav-tabs .nav-item .nav-link.active:focus,
.nav-tabs .nav-item.show .nav-link:hover,
.nav-tabs .nav-item.show .nav-link:focus {
  color: #343a40;
}
.nav-tabs .dropdown-menu {
  border-radius: 0.5rem;
}
.nav-pills .nav-link {
  transition: none;
  border-radius: var(--bs-border-radius-pill) !important;
}
.nav-pills .nav-link:hover, .nav-pills .nav-link:focus {
  color: #fff;
  isolation: auto;
}
.nav-tabs .nav-link {
  transition: none;
  border-radius: 0;
}
.nav-tabs .nav-link:hover, .nav-tabs .nav-link:focus {
  isolation: auto;
  color: #ffffff;
}
.nav-tabs .nav-link, .nav-tabs .nav-link.disabled {
  background-color: rgba(0, 0, 0, 0.2) !important;
}
.nav-tabs .nav-item:first-child > .nav-link {
  border-radius: 0.5rem 0 0 0.5rem !important;
}
.nav-tabs .nav-item:last-child > .nav-link {
  border-radius: 0 0.5rem 0.5rem 0 !important;
}
.nav-tabs .nav-item .nav-link.active,
.nav-tabs .nav-item.show .nav-link {
  transform: scale(1.1);
  border-radius: 0.5rem !important;
}
.wp-block-table.table-primary > table,
.table-primary:not(.wp-block-table) {
  --bs-table-color: var(--bs-primary-text);
  --bs-table-bg: var(--bs-primary);
  --bs-table-hover-color: var(--bs-primary-text);
}
.wp-block-table.table-secondary > table,
.table-secondary:not(.wp-block-table) {
  --bs-table-color: var(--bs-secondary-text);
  --bs-table-bg: var(--bs-secondary);
  --bs-table-hover-color: var(--bs-secondary-text);
}
.wp-block-table.table-success > table,
.table-success:not(.wp-block-table) {
  --bs-table-color: var(--bs-success-text);
  --bs-table-bg: var(--bs-success);
  --bs-table-hover-color: var(--bs-success-text);
}
.wp-block-table.table-info > table,
.table-info:not(.wp-block-table) {
  --bs-table-color: var(--bs-info-text);
  --bs-table-bg: var(--bs-info);
  --bs-table-hover-color: var(--bs-info-text);
}
.wp-block-table.table-warning > table,
.table-warning:not(.wp-block-table) {
  --bs-table-color: var(--bs-warning-text);
  --bs-table-bg: var(--bs-warning);
  --bs-table-hover-color: var(--bs-warning-text);
}
.wp-block-table.table-danger > table,
.table-danger:not(.wp-block-table) {
  --bs-table-color: var(--bs-danger-text);
  --bs-table-bg: var(--bs-danger);
  --bs-table-hover-color: var(--bs-danger-text);
}
.wp-block-table.table-light > table,
.table-light:not(.wp-block-table) {
  --bs-table-color: var(--bs-light-text);
  --bs-table-bg: var(--bs-light);
  --bs-table-hover-color: var(--bs-light-text);
}
.wp-block-table.table-dark > table,
.table-dark:not(.wp-block-table) {
  --bs-table-color: var(--bs-dark-text);
  --bs-table-bg: var(--bs-dark);
  --bs-table-hover-color: var(--bs-dark-text);
}
.text-muted {
  color: rgba(255, 255, 255, 0.5) !important;
}
