styles.css:
header.wp-block-template-part {
  margin-top: 0;
}

/* outer element border radius */
.table-bordered,
.accordion,
.list-group,
.dropdown-menu,
.modal-content,
.breadcrumb,
.carousel-inner,
.pagination.wp-block-query-pagination :first-child:not(a.page-numbers),
.page-item:first-child .page-link,
.pagination.wp-block-query-pagination :last-child:not(a.page-numbers),
.page-item:last-child .page-link,
.pagination .page-link,
.pagination .page-numbers,
.pagination .wp-block-query-pagination-previous,
.pagination .wp-block-query-pagination-next,
[class*="border-"]:not(.border-0),
figure,
figure>svg,
figure>img,
figure>table,
figure>a>img,
figure>a>svg,
[class*="wp-image-"],
.img-fluid,
.img-thumbnail,
.nav-pills .nav-link,
.form-control,
.input-group-text,
textarea,
textarea.form-control,
select,
select.form-control,
.badge,
.alert,
.progress,
.popover,
.toast,
pre,
.card,
.site-header,
.navbar,
.wp-block-quote,
.wp-block-pullquote,
.wp-block-post-terms a,
.btn-lg:not(.wp-block-button),
.btn-sm:not(.wp-block-button),
.wp-element-button,
.btn:not(.wp-block-button),
.btn-group>.btn:not(.wp-block-button),
.btn-group>.wp-element-button {
  border-radius: 2.4em 10em .33em .5em/.5em .5em .75em 11em !important;
}

/* inner element border radius */
.modal-footer,
.modal-header,
.toast-header,
.card-header,
.card-footer,
.popover-header,
.accordion-button,
.accordion-item,
.list-group-item,
.list-group-item:hover,
.dropdown-divider,
.dropdown-item:hover,
.dropdown-item,
.pagination .page-numbers.current,
.page-link.active,
.active>.page-link,
.tooltip-inner,
.table-bordered th,
.table-bordered td {
  border-radius: 2.1em 9.7em .30em .47em/.47em .47em .72em 10.7em !important;
}

/* only bottom border */
.modal-header,
.toast-header,
.card-header,
.popover-header,
img.card-img-bottom,
figure.card-img-bottom,
.card-img-bottom>img,
.card-img-bottom>a>img,
.site-header,
.navbar.fixed-top {
  border-top: 0;
  border-radius: 0 9.7em .30em .47em/.47em 0 .72em 10.7em !important;

}

/* only top border */
img.card-img-top,
figure.card-img-top,
.card-img-top>img,
.card-img-top>a>img,
.card-footer,
.modal-footer,
.navbar.fixed-bottom {
  border-bottom: 0;
  border-radius: 2.1em 9.7em 0 .47em/.47em .47em .72em 0 !important;
}

.wp-block-site-title,
.navbar-brand,
.wp-block-query-title,
.entry-title,
.toast-header,
.btn-close,
table th,
b,
strong {
  font-family: var(--wp--preset--font-family--display) !important;
}

table th,
table td {
  background-color: #fff;
  border-color: var(--bs-border-color);
  margin: 1em;
}

.table-bordered {
  overflow: hidden;
  border-spacing: 0;
  border-collapse: separate;
  border: var(--bs-border-width) solid var(--bs-border-color) !important;
}

.table-bordered th,
.table-bordered td {
  margin: 1em !important;
}

.table-bordered .table-success td,
.table-bordered .table-success th,
.table-bordered .table-success:hover td,
.table-bordered .table-success:hover th {
  color: #fff;
  background-color: #28a745;
}

.table-bordered .table-info td,
.table-bordered .table-info th,
.table-bordered .table-info:hover td,
.table-bordered .table-info:hover th {
  color: #fff;
  background-color: #17a2b8;
}

.table-bordered .table-warning td,
.table-bordered .table-warning th,
.table-bordered .table-warning:hover td,
.table-bordered .table-warning:hover th {
  color: #fff;
  background-color: #ffc107;
}

.table-bordered .table-danger td,
.table-bordered .table-danger th,
.table-bordered .table-danger:hover td,
.table-bordered .table-danger:hover th {
  color: #fff;
  background-color: #dc3545;
}

.table-dark th,
.table-dark td,
.table-dark.table-hover .table-active:hover>th,
.table-dark.table-hover .table-active:hover>td {
  background-color: #333;
}

.btn-check {
  display: inline-block;
  opacity: 0;
}

[type=checkbox]:focus,
[type=radio]:focus {
  background-color: transparent;
  border-color: none;
  outline: 0;
  box-shadow: initial;
}

[type=checkbox] {
  position: relative;
  width: 0;
  height: 0;
  -webkit-appearance: none;
  -moz-appearance: none;
  appearance: none;
  cursor: pointer;
  border: none;
}

[type=checkbox]::before {
  position: absolute;
  top: -0.1em !important;
  left: 0;
  display: inline-block;
  width: 15px !important;
  height: 16px !important;
  content: "" !important;
  border: var(--bs-border-width) solid var(--bs-border-color) !important;
  border-radius: 2px 8px 2px 4px/5px 3px 5px 3px !important;
}

[type=checkbox]:focus::before {
  box-shadow: 0 0 0 0.25rem rgba(51, 51, 51, 0.25) !important;
}

[type=checkbox]:checked::after,
[type=checkbox]:indeterminate::after {
  position: absolute;
  top: 0 !important;
  left: 0.1em !important;
  font-size: 1.5rem;
  line-height: 0.5;
  color: #333 !important;
}

[type=checkbox]:checked::after {
  content: "x" !important;
}

[type=checkbox]:indeterminate::after {
  top: 0.1em !important;
  content: "-";
}

[type=checkbox]:disabled::before {
  border: 2px solid #aaa !important;
}

[type=radio] {
  position: relative;
  width: 0;
  height: 0;
  -webkit-appearance: none;
  -moz-appearance: none;
  appearance: none;
  cursor: pointer;
  border: none;
}

[type=radio]::before {
  position: absolute;
  top: -0.1em !important;
  left: 0 !important;
  display: inline-block;
  width: 16px !important;
  height: 16px !important;
  content: "" !important;
  border: var(--bs-border-width) solid var(--bs-border-color) !important;
  border-radius: 50% 45% 40% 50%/40% 50% 50% 45% !important;
}

[type=radio]:focus::before {
  box-shadow: 0 0 0 0.25rem rgba(51, 51, 51, 0.25) !important;
}

[type=radio]:checked::before {
  background-color: #333 !important;
}

[type=radio]:disabled::before {
  border: 2px solid #aaa !important;
}

.form-check-input {
  border: none !important;
}

.form-check-input:focus {
  outline: 0;
  box-shadow: 0 0 0 0.15rem rgba(51, 51, 51, 0.25);
}

.form-switch {
  padding-left: 0 !important;
}

.form-switch .form-check-input {
  position: relative;
  margin-left: 0 !important;
}

.form-switch .form-check-input::before {
  width: 32px !important;
  border-radius: 30% 35% 30% 30%/30% 50% 30% 45% !important;
}

.form-switch .form-check-input::after {
  position: absolute;
  top: 0;
  left: 0;
  display: inline-block;
  width: 12px !important;
  height: 12px !important;
  content: "" !important;
  background-color: #fff !important;
  border: var(--bs-border-width) solid var(--bs-border-color) !important;
  border-radius: 50% 45% 40% 50%/40% 50% 50% 45% !important;
  transition: left 0.15s ease-in-out;
  box-shadow: none !important;
}

.form-switch .form-check-input:checked::after {
  top: 0 !important;
  left: 18px !important;
  background-color: transparent !important;
}

.form-switch .form-check-label {
  margin-left: 0.5em;
}

.form-check-input:focus,
.form-check-input:checked {
  background: transparent !important;
  border-color: none !important;
  box-shadow: none;
  outline: 0;

}

.form-check-input:active {
  filter: none !important;
}

.accordion,
.list-group {
  border: var(--bs-border-width) solid var(--bs-border-color) !important;
}

.accordion-item,
.list-group-item {
  border-top: var(--bs-border-width) solid var(--bs-border-color) !important;
  border-right: none !important;
  border-left: none !important;
  border-bottom: none !important;
}

.accordion-item:first-child,
.list-group-item:first-child {
  border-top: none !important;
}

.nav-tabs .nav-link {
  border-radius: 45px 15px 225px 5px/25px 225px 25px 255px !important;
}

.breadcrumb {
  border: var(--bs-border-width) solid var(--bs-border-color) !important;
}

.badge-pill {
  border-radius: 7rem 8rem 8rem 8rem/4rem 5rem 6rem 6rem !important;
}

.alert .btn-close::before {
  color: inherit !important;
}

.progress {
  border: var(--bs-border-width) solid var(--bs-border-color) !important;
}

.carousel-inner,
.card,
.toast,
.popover,
.accordion,
.list-group,
.dropdown-menu {
  overflow: hidden !important;
}

pre {
  border: var(--bs-border-width) solid var(--bs-border-color) !important;

}

.btn-close {
  background-image: none !important;
}

.btn-close::before {
  font-weight: strong !important;
 color: inherit !important;
  content: "X" !important;
}
